//This react component displays the User, Department, Organization details
import React, { Component } from "react";
import EmployeeService from "../service/EmployeeService";

class EmployeeComponent extends Component {
  //constructor to initialize component state value
  //props===> It is plain javaScript Object it get passed to the component, like parent to child component
  constructor(props) {
    super(props);

    //To define objects
    //Below object are initialized Empty
    //State object is plain javaScript Object, managed within the component
    this.state = {
      employee: {},
      department: {},
      organization: {},
    };
  }

  //Calling the Component life cycle method, within this method we are going to make the REST API Call
  //This method is called when Component gets mounted
  componentDidMount() {
    // EmployeeService.getEmployee().then((response) => {
    //   this.setState({ employee: response.data.employee });
    //   this.setState({ department: response.data.department });
    //   this.setState({ organization: response.data.organization });

    //   //To print the Objects
    //   console.log(this.state.employee);
    //   console.log(this.state.department);
    //   console.log(this.state.organization);
    EmployeeService.getEmployee().then((response) => {
      const { employee, department, organization } = response.data;

      this.setState({ employee, department, organization }, () => {
        // This runs after the state is updated
        console.log("Employee:", this.state.employee);
        console.log("Department:", this.state.department);
        console.log("Organization:", this.state.organization);
      });
    });
  }

  render() {
    return <div></div>;
  }
}

export default EmployeeComponent;
